const images = [
    'data:image/jpeg;base64,',
    'data:image/jpeg;base64,',
    'data:image/jpeg;base64,',
    'data:image/jpeg;base64,'
  ];

  const imageElement = document.querySelector('.slider-image');
  const prevButton = document.querySelector('.prev-button');
  const nextButton = document.querySelector('.next-button');

  let currentIndex = 0;

  function updateImage() {
    imageElement.src = images[currentIndex];
    currentIndex === 0? prevButton.style.display = 'none':prevButton.style.display = 'flex';
    currentIndex === images.length - 1?nextButton.style.display = 'none':nextButton.style.display = 'flex';
  }

  function showNextImage() {
    if (currentIndex < images.length - 1) {
      currentIndex++;
      updateImage();
    }
  }
  function showPrevImage() {
    if (currentIndex > 0) {
      currentIndex--;
      updateImage();
    }
  }
  prevButton.addEventListener('click', showPrevImage);
  nextButton.addEventListener('click', showNextImage);
  updateImage();